  }

  function handleClickButtonNavOrOutMenu(){
    if((menuBar && backgroundMenuBar) === false){
      setMenuBar(<MenuBar />);
      setBackgroundMenuBar(<BackgroundMenuBar onClick={handleClickBackground}/>);
    }else{
      setMenuBar(false);
      setBackgroundMenuBar(false);
    };
  };

  return (
    <header
      className="
        bg-bg_white
        h-auto
      ">
      <div className="
        grid grid-cols-2
        m-1
      ">

        <div
          id="logo-box"
          className="
            flex flex-row items-center
            h-full
          "
        >
          <div className="w-84">
            <a href="https://ionecavalcanteengenharia.com.br">
              <img src={IoneCavalcanteEngenhariaLogoWithName} alt="logoWithName" />
            </a>
          </div>

        </div>
        <ButtonNav onClick={handleClickButtonNavOrOutMenu}/>
        <NavBar />
        {menuBar}
        {backgroundMenuBar }

      </div>

      <div
        id="header-bottom-bar"
        className="
          w-full bg-primary h-1
          sm:h-1
          md:h-1p
          lg:h-1p
          xl:h-1p
        "
      ></div>
    </header>
  );
};

export default Header;
