import React, { useState } from 'react';
import NavBar from './NavBar';
import MenuBar from './MenuBar';
import BackgroundMenuBar from './BackgroundMenuBar';
import ButtonNav from './ButtonNav';
import '../../styles/output.css';

import IoneCavalcanteEngenhariaLogoWithName from '../images/header/LogoWithLogoName.svg';

function Header() {

  const [MenuBar, setMenuBar] = useState(null);
  const [BackgroundMenuBar, setBackgroundMenuBar] = useState(null)

  function handleClickButtonNav(){
    if((MenuBar && BackgroundMenuBar)){
      setMenuBar(<MenuBar />);
      setBackgroundMenuBar(<BackgroundMenuBar />);
    }else{
      setMenuBar(null);
      setBackgroundMenuBar(null);
    };
  };

  return (
    <header
      className="
        bg-bg_white
        h-auto
      ">
      <div className="
        grid grid-cols-2
        m-1
      ">

        <div
          id="logo-box"
          className="
            flex flex-row items-center
            h-full
          "
        >
          <div className="w-84">
            <a href="https://ionecavalcanteengenharia.com.br">
              <img src={IoneCavalcanteEngenhariaLogoWithName} alt="logoWithName" />
            </a>
          </div>

        </div>
        <ButtonNav onClick={handleClickButtonNav}/>
        <NavBar />
        {MenuBar}
        {BackgroundMenuBar}

      </div>

      <div
        id="header-bottom-bar"
        className="
          w-full bg-primary h-1
          sm:h-1
          md:h-1p
          lg:h-1p
          xl:h-1p
        "
      ></div>
    </header>
  );
};

export default Header;
